import React from "react";
import { Box, Card, CardContent, Typography, Avatar, Button, Grid } from "@mui/material";
import 'bootstrap/dist/css/bootstrap.min.css';
import { useNavigate } from "react-router-dom";
// Helper function to generate initials
const getInitials = (name) => {
    const nameParts = name.split(" ");
    return (
        (nameParts[0]?.[0] || "") + (nameParts[nameParts.length - 1]?.[0] || "")
    ).toUpperCase();
};



const Home = ({ userName = "John Doe", onLogout }) => {
    const navigate = useNavigate();
    const initials = getInitials(userName);

    const handleCardClick = (type) => {
        switch (type) {
            case "profile":
                navigate("./profile");
                // alert("Navigating to Profile...");
                // // Navigation logic to Profile component
                break;
            case "view_student":
                alert("Navigating to View Student...");
                // Navigation logic to View Student component
                break;
            case "add_student":
                alert("Navigating to Add Student...");
                // Navigation logic to Add Student component
                break;
            case "edit_student":
                alert("Navigating to Edit Student...");
                // Navigation logic to Edit Student component
                break;
            default:
                alert("Invalid action");
        }
    };

    return (
        <Box
            sx={{
                padding: 3,
                backgroundColor: "#f5f5f5",
                minHeight: "100vh",
                display: "flex",
                flexDirection: "column",
            }}
        >
            {/* Title Section */}
            <Box
                display="flex"
                alignItems="center"
                justifyContent="space-between"
                marginBottom={4}
            >
                <Box display="flex" alignItems="center">
                    <Avatar
                        sx={{
                            bgcolor: "#1976d2",
                            color: "#fff",
                            width: 56,
                            height: 56,
                            fontSize: "1.5rem",
                            marginRight: 2,
                        }}
                    >
                        {initials}
                    </Avatar>
                    <Typography variant="h5" fontWeight="bold">
                        Welcome, {userName}
                    </Typography>
                </Box>
            </Box>

            {/* Card Section */}
            <Grid container spacing={3}>
                {[
                    { title: "Profile", description: "View your profile details." },
                    { title: "View Students", description: "View all students." },
                    { title: "Add Student", description: "Create a new student." },
                    { title: "Edit Student", description: "Update student details." },
                ].map((card, index) => (
                    <Grid item xs={12} sm={6} md={3} key={index}>
                        <Card
                            sx={{
                                height: 150,
                                display: "flex",
                                flexDirection: "column",
                                justifyContent: "center",
                                alignItems: "center",
                                cursor: "pointer",
                                transition: "transform 0.2s",
                                "&:hover": {
                                    transform: "scale(1.05)",
                                },
                            }}
                            onClick={() => handleCardClick(card.type)}
                        >
                            <CardContent>
                                <Typography variant="h6" fontWeight="bold" align="center">
                                    {card.title}
                                </Typography>
                                <Typography
                                    variant="body2"
                                    color="textSecondary"
                                    align="center"
                                    mt={1}
                                >
                                    {card.description}
                                </Typography>
                            </CardContent>
                        </Card>
                    </Grid>
                ))}
            </Grid>

            {/* Logout Button */}
            <Box
                sx={{
                    position: "fixed",
                    bottom: 16,
                    right: 16,
                }}
            >
                <Button
                    variant="contained"
                    color="error"
                    size="large"
                    onClick={onLogout}
                >
                    Logout
                </Button>
            </Box>
        </Box>
    );
};

export default Home;
