import React from "react";
import { useLocation, useNavigate } from "react-router-dom";
import { AppBar, Toolbar, Typography, IconButton, Avatar, Box, Divider } from "@mui/material";
import ArrowBackIcon from "@mui/icons-material/ArrowBack";

const Header = ({ userName = "John Doe" }) => {
    const location = useLocation();
    const navigate = useNavigate();

    // Check if the current screen is the login screen
    const isLoginScreen = location.pathname === "/";

    // Check if the current screen is the dashboard
    const isDashboard = location.pathname === "/home";

    if (isLoginScreen) {
        return null; // No header on the login screen
    }

    return (
        <AppBar
            position="static"
            sx={{
                backgroundColor: "#FFFFFF",
                boxShadow: "none",
                borderBottom: "1px solid #E0E0E0", // Light gray bottom border
            }}
        >
            <Toolbar>
                {/* Back Button */}
                {!isDashboard && (
                    <IconButton edge="start" onClick={() => navigate(-1)} sx={{ color: "#000" }}>
                        <ArrowBackIcon />
                    </IconButton>
                )}

                {/* Title */}
                <Typography
                    variant="h6"
                    sx={{
                        flexGrow: 1,
                        color: "#000", // Black title
                        textTransform: "capitalize",
                    }}
                >
                    {isDashboard ? "Dashboard" : location.pathname.slice(1).replace("-", " ")}
                </Typography>

                {/* Avatar and Username */}
                {isDashboard && (
                    <Box sx={{ display: "flex", alignItems: "center" }}>
                        <Avatar
                            sx={{
                                bgcolor: "secondary.main",
                                mr: 1,
                                color: "#FFF",
                                backgroundColor: "#1976D2", // Primary blue
                            }}
                        >
                            {userName.split(" ")[0][0]}
                            {userName.split(" ")[1]?.[0]}
                        </Avatar>
                        <Typography variant="body1" sx={{ color: "#000" }}>
                            {userName}
                        </Typography>
                    </Box>
                )}
            </Toolbar>
            <Divider />
        </AppBar>
    );
};

export default Header;
