import React from "react";
import { useLocation, useNavigate } from "react-router-dom";
import { AppBar, Toolbar, Typography, IconButton, Avatar, Box } from "@mui/material";
import ArrowBackIcon from "@mui/icons-material/ArrowBack";

const Header = ({ userName = "John Doe" }) => {
    const location = useLocation();
    const navigate = useNavigate();

    const isDashboard = location.pathname === "/home"; // Adjust route as needed

    const handleBackClick = () => {
        navigate(-1); // Go back to the previous screen
    };

    return (
        <AppBar position="static" color="primary">
            <Toolbar>
                {/* Back Button */}
                {!isDashboard && (
                    <IconButton edge="start" color="inherit" onClick={handleBackClick}>
                        <ArrowBackIcon />
                    </IconButton>
                )}

                {/* Title */}
                <Typography variant="h6" sx={{ flexGrow: 1 }}>
                    {isDashboard ? "Dashboard" : location.pathname.slice(1).replace("-", " ")} {/* Example for dynamic title */}
                </Typography>

                {/* Avatar and Username */}
                <Box sx={{ display: "flex", alignItems: "center" }}>
                    <Avatar sx={{ bgcolor: "secondary.main", mr: 1 }}>
                        {userName.split(" ")[0][0]}
                        {userName.split(" ")[1][0]}
                    </Avatar>
                    <Typography variant="body1">{userName}</Typography>
                </Box>
            </Toolbar>
        </AppBar>
    );
};

export default Header;
