import React, { useState } from "react";
import {
    TextField,
    Button,
    Typography,
    Container,
    Box,
    Table,
    TableBody,
    TableCell,
    TableContainer,
    TableHead,
    TableRow,
    Paper,
} from "@mui/material";
import Pagination from "@mui/material/Pagination";

const AddStudent = () => {
    // State for student details
    const [students, setStudents] = useState([]);
    const [name, setName] = useState("");
    const [regNumber, setRegNumber] = useState("");
    const [standard, setStandard] = useState("");
    const [dob, setDob] = useState("");
    const [classTeacher, setClassTeacher] = useState("");
    const [page, setPage] = useState(1);

    // Number of items per page
    const rowsPerPage = 5;

    const handleSubmit = (e) => {
        e.preventDefault();
        const newStudent = {
            id: students.length + 1,
            name,
            regNumber,
            standard,
            dob,
            classTeacher,
        };

        setStudents([...students, newStudent]);
        setName("");
        setRegNumber("");
        setStandard("");
        setDob("");
        setClassTeacher("");
    };

    // Pagination logic
    const handleChangePage = (event, value) => {
        setPage(value);
    };

    const displayStudents = students.slice((page - 1) * rowsPerPage, page * rowsPerPage);

    return (
        <Container>
            <Typography variant="h4" gutterBottom>
                Add Student
            </Typography>

            <form onSubmit={handleSubmit}>
                <Box mb={2}>
                    <TextField
                        label="Name"
                        fullWidth
                        value={name}
                        onChange={(e) => setName(e.target.value)}
                        required
                    />
                </Box>
                <Box mb={2}>
                    <TextField
                        label="Reg Number"
                        fullWidth
                        value={regNumber}
                        onChange={(e) => setRegNumber(e.target.value)}
                        required
                    />
                </Box>
                <Box mb={2}>
                    <TextField
                        label="Standard"
                        fullWidth
                        value={standard}
                        onChange={(e) => setStandard(e.target.value)}
                        required
                    />
                </Box>
                <Box mb={2}>
                    <TextField
                        label="Date of Birth"
                        fullWidth
                        type="date"
                        value={dob}
                        onChange={(e) => setDob(e.target.value)}
                        InputLabelProps={{
                            shrink: true,
                        }}
                        required
                    />
                </Box>
                <Box mb={2}>
                    <TextField
                        label="Class Teacher"
                        fullWidth
                        value={classTeacher}
                        onChange={(e) => setClassTeacher(e.target.value)}
                        required
                    />
                </Box>

                <Button variant="contained" color="primary" type="submit">
                    Add Student
                </Button>
            </form>


        </Container>
    );
};

export default AddStudent;
